            log('INFO', 'åˆ·æ–°é¡µé¢è§¦å‘'); // æ–°å¢æ—¥å¿—
            currentTask = 'åˆ·æ–°é¡µé¢';
            taskStartTime = Date.now();
            taskDuration = refreshDelay;
            nextTask = 'æ‰§è¡Œå·¥ä½œæµ';
            lastRefresh = Date.now();
